      const Upcol = board.columns && board.columns.map(b =>{

        if(b.name == updatecolumn.name){
          b = updatecolumn
        }

        //console.log(updatecolumn.name)
        //console.log("b=>",b)
        i++
        //console.log("I =>",i)
        return b

      })

      //console.log("ppp",Upcol)
      //console.log("bbb",updateBoard)

      //updateBoard.columns = Upcol
