import  React, { useState , useEffect} from 'react'
import { useSelector , useDispatch } from 'react-redux';
import boardsSlice, {fetchData} from "../redux/boardsSlice"
import Home from "../components/Home";
import Header from "../components/Header";
import EmptyBoard from '../components/EmptyBoard';
import { updateData } from "../redux/boardsSlice";
import store from '../redux/store';

function Account() {

  const [isBoardModalOpen, setIsBoardModalOpen] = useState(false);
  const [refreshCount, setRefreshCount] = useState(0);
  const dispatch = useDispatch();

  const boards = useSelector((state) => {
    return state.boards.boards
  });
  
  useEffect(() => {

   dispatch(fetchData())
   const intervalId = setInterval(() => {
    setRefreshCount(prevCount => prevCount + 1);
    const updateBoards = store.getState().boards.boards;
      dispatch(updateData(updateBoards));
  }, 1000);

  return () => {
    clearInterval(intervalId);
  };
   
  
  }, [])

  const activeBoard = boards && boards.find((board) => board.isActive);
  if(!activeBoard  && boards && boards.length > 0){
    dispatch(boardsSlice.actions.setBoardActive({ index: 0 }));
  }
  

  return (

    <div className=" overflow-hidden  overflow-x-scroll">
      <>
        {boards && boards.length > 0 ?
        <>
          
          <Header
          setIsBoardModalOpen={setIsBoardModalOpen}
          isBoardModalOpen={isBoardModalOpen}
          />
          <Home
          setIsBoardModalOpen={setIsBoardModalOpen}
          isBoardModalOpen={isBoardModalOpen}
          />
        </>
        :
        <>
          
          <EmptyBoard type='add'/>
        </>
        }
        
      </>
    </div>
    
  )
}

export default Account
