import  React, { useState , useEffect} from 'react'
import { useSelector , useDispatch } from 'react-redux';
import boardsSlice, {fetchData} from "../redux/boardsSlice"
import Home from "../components/Home";
import Header from "../components/Header";
import EmptyBoard from '../components/EmptyBoard';


function Account() {

  const [isBoardModalOpen, setIsBoardModalOpen] = useState(false);
  const dispatch = useDispatch();
  
  //if (!activeBoard && boards.length > 0)
  //  dispatch(boardsSlice.actions.setBoardActive({ index: 0 }));
  
  

  useEffect(() => {
    
   dispatch(fetchData())
  
  }, [])
  

  const { boards , status, error, loading   } = useSelector((state) => state.boards) ;

  const activeBoard = boards?.data?.find((board) => board.isActive);
  console.log('boards => ',boards.data)
  console.log('activeBoard => ',activeBoard)


  

  return (

    <div className=" overflow-hidden  overflow-x-scroll">
      <>
        {boards.length > 0 ?
        <>
        <Header
          setIsBoardModalOpen={setIsBoardModalOpen}
          isBoardModalOpen={isBoardModalOpen}
        />
        <Home
          setIsBoardModalOpen={setIsBoardModalOpen}
          isBoardModalOpen={isBoardModalOpen}
        />
        </>
        :
        <>
          <EmptyBoard type='add'/>
        </>
      }
        
      </>
    </div>


    /*<div className='w-full h-screen bg-[#1a1a1a] text-white flex justify-center items-center'>
        <h2 className='text-3xl'>ACCOUNT</h2>
    </div>*/
    
  )
}

export default Account
